import React from "react";
import styled from "styled-components";
import "../components/App.css";
import { useState, useEffect } from "react";
import { getKlubovi, getFull, exportCSV, exportJSON, getIgraci } from "../utils/fetch";


import DataTable from 'react-data-table-component';




const Datatable = (props) => {
  const [klubovi, setKluboviList] = useState([]);
  const [columns, setColumns] = useState([]);
  const [filter, setFilter] = useState("");
  const [filterOption, setFilterOption] = useState("wildcard");
  
  
  
  //console.log(columns)
  let optionArray=[]
  Object.keys(klubovi[0] || {}).forEach(key => optionArray.push(<option key={key} value={key}>{key}</option>))
  
  
  if(filterOption === "wildcard"){

    var kluboviArray = klubovi.length > 0 ? 
      klubovi.filter((klub)=>{
        for(let value in klub){
            if(klub[value] === null) continue;
            if(klub[value].toString().toLowerCase().includes(filter.toString().toLowerCase())) return true;
        }
        return false;
      }) : []
  }
  else{
    console.log(filterOption)
    var kluboviArray = klubovi.length>0 ? klubovi.filter((value)=>{
        return value[filterOption].toString().toLowerCase().includes(filter.toString().toLowerCase())
        }) : [];
  }
 
  
  useEffect(() => {
    const res = getKlubovi().then((item) => {
      setKluboviList(item.klubovi);
      let columnsTemp=[];
      
      Object.keys(item.klubovi[0]).forEach(key => columnsTemp.push({name: key, selector: (row)=>row[key]}))
      console.log(columnsTemp)
      setColumns(columnsTemp);
    });
  }, []);
   
  let handleChange=(event)=>{
    console.log(event.target.value)
    setFilter(event.target.value);

  }
  return (<>
    <center><a href="/"><img class="logo" src="../img/lopta.png" alt="lopta"/></a></center>
    <TableSection>
      <div>Prikaz podataka:</div>
        <form onSubmit={(e) => e.preventDefault()}>
            <select value={filterOption} onChange={(e) => setFilterOption(e.target.value)}>
                <option value={"wildcard"}>svi podatci</option>
                {optionArray}
            </select>
          <input type="text" name="filter" id="filter" placeholder="filtriraj" value={filter} onChange={(event)=>{handleChange(event)}}></input>
        </form>

    </TableSection>
        <DataTable
            columns={columns}
            data={kluboviArray}
            pagination
            highlightOnHover
          
        />
    </>
  );
  
};

export default Datatable;

//styles
const TableSection= styled.section`
    margin-left: 10px;

`;

const ExportButton=styled.button`
  padding:5px;
  margin: 5px;
`;
